window.onload=()=>{const e=document.querySelector(".hero nav"),t=document.querySelector("body");t.addEventListener("click",n=>{if(window.innerWidth>=1024)return;const i=t.getBoundingClientRect(),o=n.clientX-i.left,c=n.clientY;o>=i.width-60&&o<=i.width-20&&c>=20&&c<=60&&e.classList.toggle("is-open")})};